import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Settings.css';

const Settings = ({ user, toggleDarkMode, darkMode }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
    window.location.reload();
  };

  const resetBMR = () => {
    const userData = JSON.parse(localStorage.getItem('user')) || {};
    userData.bmr = 0;
    localStorage.setItem('user', JSON.stringify(userData));
    alert('BMR values have been reset.');
  };

  return (
    <div className="container">
      <h1 className="page-title">Settings</h1>

      <div className="card">
        <div className="settings-option">
          <div className="option-label">
            <h3>Dark Mode</h3>
            <p>Switch between light and dark theme</p>
          </div>
          <label className="toggle-switch">
            <input 
              type="checkbox" 
              checked={darkMode}
              onChange={toggleDarkMode}
            />
            <span className="slider"></span>
          </label>
        </div>

        <div className="settings-option">
          <div className="option-label">
            <h3>Reset BMR Values</h3>
            <p>Clear your saved BMR calculation</p>
          </div>
          <button className="btn btn-secondary" onClick={resetBMR}>Reset</button>
        </div>

        <div className="settings-option">
          <div className="option-label">
            <h3>Update Profile Picture</h3>
            <p>Change your profile image</p>
          </div>
          <button className="btn btn-secondary">Upload</button>
        </div>

        <div className="settings-option">
          <div className="option-label">
            <h3>Sign Out</h3>
            <p>Log out of your account</p>
          </div>
          <button className="btn btn-danger" onClick={handleLogout}>Sign Out</button>
        </div>
      </div>
    </div>
  );
};

export default Settings;
