import React, { useState, useEffect } from 'react';
import './Home.css';

const Home = ({ user }) => {
  const [bmrData, setBmrData] = useState({
    weight: '',
    height: '',
    age: '',
    gender: 'male',
    activityLevel: 'sedentary'
  });
  const [bmrResult, setBmrResult] = useState(user?.bmr || 0);
  const [totalCalories, setTotalCalories] = useState(user?.totalCaloriesBurned || 0);

  useEffect(() => {
    // Load saved BMR and calories from localStorage
    const userData = JSON.parse(localStorage.getItem('user'));
    if (userData) {
      setBmrResult(userData.bmr || 0);
      setTotalCalories(userData.totalCaloriesBurned || 0);
    }
  }, []);

  const handleChange = (e) => {
    setBmrData({
      ...bmrData,
      [e.target.name]: e.target.value
    });
  };

  const calculateBMR = () => {
    let bmr = 0;

    // BMR Formula
    if (bmrData.gender === 'male') {
      bmr = 88.362 + (13.397 * parseFloat(bmrData.weight)) + (4.799 * parseFloat(bmrData.height)) - (5.677 * parseFloat(bmrData.age));
    } else {
      bmr = 447.593 + (9.247 * parseFloat(bmrData.weight)) + (3.098 * parseFloat(bmrData.height)) - (4.330 * parseFloat(bmrData.age));
    }

    // Adjust for activity level
    switch (bmrData.activityLevel) {
      case 'sedentary':
        bmr *= 1.2;
        break;
      case 'light':
        bmr *= 1.375;
        break;
      case 'moderate':
        bmr *= 1.55;
        break;
      case 'active':
        bmr *= 1.725;
        break;
      case 'veryActive':
        bmr *= 1.9;
        break;
      default:
        bmr *= 1.2;
    }

    // Round to nearest whole number
    bmr = Math.round(bmr);
    setBmrResult(bmr);

    // Save to localStorage
    const userData = JSON.parse(localStorage.getItem('user')) || {};
    userData.bmr = bmr;
    localStorage.setItem('user', JSON.stringify(userData));
  };

  return (
    <div className="container">
      <h1 className="page-title">Welcome to AI Fitness Trainer</h1>

      {/* Calorie Counter */}
      <div className="calorie-counter">
        <h2>Total Calories Burned</h2>
        <div className="calorie-value">{totalCalories}</div>
        <p>Keep up the good work!</p>
      </div>

      {/* BMR Calculator */}
      <div className="card bmr-calculator">
        <h2>Calculate Your BMR</h2>
        <p>BMR (Basal Metabolic Rate) is the number of calories your body needs at rest.</p>

        <div className="form-group">
          <label htmlFor="weight">Weight (kg)</label>
          <input
            type="number"
            id="weight"
            name="weight"
            className="form-control"
            value={bmrData.weight}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="height">Height (cm)</label>
          <input
            type="number"
            id="height"
            name="height"
            className="form-control"
            value={bmrData.height}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="age">Age</label>
          <input
            type="number"
            id="age"
            name="age"
            className="form-control"
            value={bmrData.age}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="gender">Gender</label>
          <select
            id="gender"
            name="gender"
            className="form-control"
            value={bmrData.gender}
            onChange={handleChange}
          >
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="activityLevel">Activity Level</label>
          <select
            id="activityLevel"
            name="activityLevel"
            className="form-control"
            value={bmrData.activityLevel}
            onChange={handleChange}
          >
            <option value="sedentary">Sedentary (little or no exercise)</option>
            <option value="light">Light (exercise 1-3 days/week)</option>
            <option value="moderate">Moderate (exercise 3-5 days/week)</option>
            <option value="active">Active (exercise 6-7 days/week)</option>
            <option value="veryActive">Very Active (hard exercise daily)</option>
          </select>
        </div>

        <button onClick={calculateBMR} className="btn btn-primary">Calculate BMR</button>

        {bmrResult > 0 && (
          <div className="bmr-result">
            <h3>Your Daily Calorie Needs: {bmrResult} calories</h3>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
