import React from 'react';
import { Link } from 'react-router-dom';

const Signup = ({ setUser }) => {
  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-500 to-purple-600">
      <div className="bg-white p-8 rounded-lg shadow-lg max-w-md w-full">
        <h2 className="text-3xl font-bold text-center mb-6 text-gray-800">Join AI Fitness</h2>
        <p className="text-center text-gray-600 mb-8">Create your account to start tracking your fitness</p>
        <div className="text-center">
          <p className="text-gray-600 mb-4">We use Google authentication for secure sign-up</p>
          <Link
            to="/login"
            className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-3 px-6 rounded-lg transition duration-300 inline-block"
          >
            Sign In with Google
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Signup;
