import React, { useState } from 'react';
import './ExerciseAI.css';

const ExerciseAI = ({ user }) => {
  const [selectedExercise, setSelectedExercise] = useState(null);
  const [showOptions, setShowOptions] = useState(false);

  const exercises = [
    { id: 'pushups', name: 'Push-ups', image: 'ðŸ’ª', caloriesPerMin: 8 },
    { id: 'pullups', name: 'Pull-ups', image: 'ðŸ‹ï¸', caloriesPerMin: 10 },
    { id: 'squats', name: 'Squats', image: 'ðŸ¦µ', caloriesPerMin: 8 },
    { id: 'russiantwist', name: 'Russian Twist', image: 'ðŸ”„', caloriesPerMin: 7 },
    { id: 'legraises', name: 'Leg Raises', image: 'ðŸ¦¿', caloriesPerMin: 6 },
    { id: 'planks', name: 'Planks', image: 'ðŸ§˜', caloriesPerMin: 5 },
    { id: 'lateralrise', name: 'Lateral Rise', image: 'ðŸ¤¸', caloriesPerMin: 6 },
    { id: 'chestbutterfly', name: 'Chest Butterfly', image: 'ðŸ¦‹', caloriesPerMin: 9 },
    { id: 'bicep', name: 'Bicep Curls', image: 'ðŸ’ª', caloriesPerMin: 7 }
  ];

  const handleExerciseSelect = (exercise) => {
    setSelectedExercise(exercise);
    setShowOptions(true);
  };

  const handleUpload = () => {
    // This is where your friend's model integration would happen
    alert('Upload functionality will be integrated with AI model');
    // Comment for integration: Replace this with actual file upload and model processing
    updateCalories(50); // Example: 50 calories burned
  };

  const handleCamera = () => {
    // This is where your friend's model integration would happen
    alert('Camera functionality will be integrated with AI model');
    // Comment for integration: Replace this with camera access and model processing
    updateCalories(75); // Example: 75 calories burned
  };

  const updateCalories = (caloriesBurned) => {
    // Update total calories burned
    const userData = JSON.parse(localStorage.getItem('user')) || {};
    userData.totalCaloriesBurned = (userData.totalCaloriesBurned || 0) + caloriesBurned;
    localStorage.setItem('user', JSON.stringify(userData));

    alert(`Great job! You burned ${caloriesBurned} calories.`);
  };

  const handleBack = () => {
    setSelectedExercise(null);
    setShowOptions(false);
  };

  return (
    <div className="container">
      <h1 className="page-title">Exercise AI</h1>

      {!selectedExercise ? (
        <>
          <h3 className="section-subtitle">Select an exercise to begin</h3>
          <div className="exercise-grid">
            {exercises.map((exercise) => (
              <div 
                key={exercise.id} 
                className="exercise-box"
                onClick={() => handleExerciseSelect(exercise)}
              >
                <div className="exercise-icon">{exercise.image}</div>
                <h3>{exercise.name}</h3>
              </div>
            ))}
          </div>
        </>
      ) : (
        <div className="selected-exercise">
          <button className="btn btn-secondary back-btn" onClick={handleBack}>
            &larr; Back to Exercises
          </button>

          <h2>{selectedExercise.name}</h2>
          <div className="exercise-icon large">{selectedExercise.image}</div>

          {showOptions && (
            <>
              <p className="exercise-instruction">Choose an option to track your exercise:</p>
              <div className="exercise-options">
                <button className="btn btn-primary option-btn" onClick={handleUpload}>
                  Upload Video/Image
                </button>
                <button className="btn btn-secondary option-btn" onClick={handleCamera}>
                  Use Camera
                </button>
              </div>
            </>
          )}

          <div className="integration-note">
            {/* This comment block is for your friend's integration */}
            {/* 
              INTEGRATION NOTES:
              1. Replace handleUpload function with actual file upload and AI model processing
              2. Replace handleCamera function with camera access and real-time AI model processing
              3. The updateCalories function should be called with the actual calories burned from the model
              4. Make sure to persist the calories data in localStorage or your database
            */}
          </div>
        </div>
      )}
    </div>
  );
};

export default ExerciseAI;
