import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './index.css';

// Auth Components
import Login from './components/Auth/Login';
import Signup from './components/Auth/Signup';

// Main Components
import Home from './components/Home/Home';
import ExerciseAI from './components/ExerciseAI/ExerciseAI';
import Settings from './components/Settings/Settings';
import Profile from './components/Profile/Profile';
import Navbar from './components/Navbar/Navbar';

function App() {
  const [user, setUser] = useState(null);
  const [darkMode, setDarkMode] = useState(false);

  // Check if user is logged in
  useEffect(() => {
    const loggedInUser = localStorage.getItem('user');
    if (loggedInUser) {
      setUser(JSON.parse(loggedInUser));
    }
  }, []);

  // Toggle dark mode
  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.body.classList.toggle('dark');
  };

  // Temporary bypass for auth
  const bypassAuth = false; // Set to false to enable auth

  return (
    <div className={`${darkMode ? 'dark' : ''}`}>
      <Router>
        {(user || bypassAuth) && <Navbar />}
        <Routes>
          <Route path="/login" element={(user || bypassAuth) ? <Navigate to="/" /> : <Login setUser={setUser} />} />
          <Route path="/signup" element={(user || bypassAuth) ? <Navigate to="/" /> : <Signup setUser={setUser} />} />
          <Route path="/" element={(user || bypassAuth) ? <Home user={user} /> : <Navigate to="/login" />} />
          <Route path="/exercise" element={(user || bypassAuth) ? <ExerciseAI user={user} /> : <Navigate to="/login" />} />
          <Route path="/settings" element={(user || bypassAuth) ? <Settings user={user} toggleDarkMode={toggleDarkMode} darkMode={darkMode} /> : <Navigate to="/login" />} />
          <Route path="/profile" element={(user || bypassAuth) ? <Profile user={user} /> : <Navigate to="/login" />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
