import React, { useState, useEffect } from 'react';

const Home = ({ user }) => {
  const [bmr, setBmr] = useState(user?.bmr || 0);
  const [totalCalories, setTotalCalories] = useState(0);
  const [notes, setNotes] = useState(localStorage.getItem('fitnessNotes') || '');
  const [showBmrCalculator, setShowBmrCalculator] = useState(!user?.bmr || user.bmr === 0);
  const [formData, setFormData] = useState({
    age: '',
    weight: '',
    height: '',
    gender: 'male'
  });

  useEffect(() => {
    fetchTotalCalories();
  }, []);

  const fetchTotalCalories = async () => {
    try {
      // For demo purposes, set a static value
      setTotalCalories(250);
    } catch (error) {
      console.error('Error fetching total calories:', error);
    }
  };

  const calculateBMR = () => {
    const { age, weight, height, gender } = formData;
    if (!age || !weight || !height) return;

    let calculatedBmr;
    if (gender === 'male') {
      calculatedBmr = 88.362 + (13.397 * weight) + (4.799 * height) - (5.677 * age);
    } else {
      calculatedBmr = 447.593 + (9.247 * weight) + (3.098 * height) - (4.330 * age);
    }

    setBmr(Math.round(calculatedBmr));
  };

  const saveBMR = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5003/api/profile/bmr', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ bmr })
      });

      if (response.ok) {
        const result = await response.json();
        // Update localStorage with new BMR
        const user = JSON.parse(localStorage.getItem('user') || '{}');
        user.bmr = result.bmr;
        user.age = formData.age;
        user.weight = formData.weight;
        user.height = formData.height;
        user.gender = formData.gender;
        localStorage.setItem('user', JSON.stringify(user));
        alert('BMR saved successfully! ✅');
        // Hide calculator after saving
        setShowBmrCalculator(false);
      } else {
        throw new Error('Failed to save BMR');
      }
    } catch (error) {
      console.error('Error saving BMR:', error);
      alert('Failed to save BMR');
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-8">Welcome back, {user?.name || 'User'}!</h1>

        {/* Total Calories Burned */}
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6 mb-8">
          <h2 className="text-2xl font-semibold text-gray-800 dark:text-white mb-4">Total Calories Burned</h2>
          <div className="text-4xl font-bold text-green-600 dark:text-green-400">{totalCalories} kcal</div>
        </div>

        {/* Notes Section */}
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6 mb-8">
          <h2 className="text-2xl font-semibold text-gray-800 dark:text-white mb-4">My Notes</h2>
          <textarea
            value={notes}
            onChange={(e) => {
              setNotes(e.target.value);
              localStorage.setItem('fitnessNotes', e.target.value);
            }}
            className="w-full h-32 px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
            placeholder="Write your fitness notes here..."
          ></textarea>
        </div>

        {/* BMR Display */}
        {showBmrCalculator ? (
          <div className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6">
            <h2 className="text-2xl font-semibold text-gray-800 dark:text-white mb-6">BMR Calculator</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">Age</label>
                  <input
                    type="number"
                    name="age"
                    value={formData.age}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                    placeholder="Enter your age"
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">Weight (kg)</label>
                  <input
                    type="number"
                    name="weight"
                    value={formData.weight}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                    placeholder="Enter your weight"
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">Height (cm)</label>
                  <input
                    type="number"
                    name="height"
                    value={formData.height}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                    placeholder="Enter your height"
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">Gender</label>
                  <select
                    name="gender"
                    value={formData.gender}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                  >
                    <option value="male">Male</option>
                    <option value="female">Female</option>
                  </select>
                </div>
                <button
                  onClick={calculateBMR}
                  className="w-full bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-md transition duration-300"
                >
                  Calculate BMR
                </button>
              </div>
              <div className="flex flex-col justify-center items-center">
                <div className="text-center">
                  <h3 className="text-lg font-medium text-gray-700 dark:text-gray-300 mb-2">Your BMR</h3>
                  <div className="text-3xl font-bold text-blue-600 dark:text-blue-400 mb-4">{bmr} kcal/day</div>
                  {bmr > 0 && (
                    <button
                      onClick={saveBMR}
                      className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded-md transition duration-300"
                    >
                      Save BMR
                    </button>
                  )}
                </div>
              </div>
            </div>
          </div>
        ) : (
          <div className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6">
            <h2 className="text-2xl font-semibold text-gray-800 dark:text-white mb-4">Your BMR</h2>
            <div className="text-center">
              <div className="text-4xl font-bold text-blue-600 dark:text-blue-400 mb-4">{bmr} kcal/day</div>
              <button
                onClick={() => setShowBmrCalculator(true)}
                className="bg-gray-500 hover:bg-gray-600 text-white font-bold py-2 px-4 rounded-md transition duration-300"
              >
                Recalculate BMR
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
